site.headStyle:
html, body { scroll-behavior: smooth; }
@media(min-width: 481px) {
    div.hero {
    min-height: 100vh;
    }
}
div.full-width {
  width: 95%;
}
.silex-editor div.hidden {
    border: 1px solid red;
}

.open-jobs {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  font-size: 56px;
  line-height: 56px;
  /* identical to box height, or 100% */


  color: #000000;

  mix-blend-mode: normal;
}
.open-jobs-text {
  font-family: 'Mulish';
  font-style: normal;
  font-weight: 300;
  font-size: 18px;
  line-height: 30px;
  /* or 167% */

  text-align: center;

  color: #7D7987;

  mix-blend-mode: normal;
}
.job-card {
  box-shadow: 10px 40px 50px rgba(229, 233, 246, 0.4);
  border-radius: 20px;
}
.job-card-title {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 150%;
  /* identical to box height, or 36px */


  /* gray/900 */

  color: #18181B;
}
.job-card-rate {
  font-family: 'Amazon Ember';
  font-style: normal;
  font-weight: 400;
  font-size: 24px;
  line-height: 24px;
  /* identical to box height, or 100% */

  letter-spacing: -0.5px;

  color: #3F3F46;
}
.job-card-tags {
  font-family: 'Amazon Ember';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 150%;
  /* identical to box height, or 24px */


  /* gray/700 */

  color: #3F3F46;
}
.job-card-skill-card {
  border-radius: 7px;
}
.job-card-skill-card-text {
  font-family: 'Amazon Ember';
  font-style: normal;
  font-weight: 400;
  font-size: 14px;
  line-height: 22px;
  /* identical to box height, or 160% */


  color: #1F1F1F;
}

site.headScript:
/*
 * animation widget from here: https://github.com/silexlabs/Silex/issues/443
 */
$(function() {
    var isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
    if(!isMobile) {
        window.sr = ScrollReveal({
            distance: '100px'
        });
        sr.reveal('.from-left', { origin:  'left'});
        sr.reveal('.from-right', { origin:  'right'});
        sr.reveal('.from-top', { origin:  'top'});
        sr.reveal('.from-bottom', { origin:  'bottom'});
    }
})
